import React from 'react';
import './App.css';

//creating class component
class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      items:[],
      currentItem:{
        text: '',
        key: ''
      }
    }
    this.handleInput = this.handleInput.bind(this);
    this.addItem = this.addItem.bind(this);
  }

  //define methods
  handleInput(e){
    this.state({
      currentItem:{
        text: e.target.value,
        key: Date.now()
      }
    })
  }
    addItem(e){
      e.preventDefault(); //prevent button from refreshing on every click
      const newItem = this.state.currentItem;
      if (newItem.text !==''){
        const newItems = [...this.state.items, newItems];
        this.setState({
          items: newItems,

        })
      }
    }

  render(){
    return(
      <div className = 'App'>
        <header>
        <form id = 'todo_form' onSubmit = {this.addItem}>
          <input type='text' placeholder = 'Enter text' value = {this.state.currentItem.text} onChange = {this.handleInput}

          />

          <button type = 'submit'>Add</button>

        </form>
      </header>
      </div>
    );
  }
}

export default App;
